    {
        var solution = new ArraySolution();
        var input = new int[] { 1, 2, 3 };
        var expected = new int[] { 3, 2, 1 };
        var result = solution.ReverseArray(input);
        Assert.Equal(expected, result);
    }

    [Fact]
    public void FindMax_ShouldFindMaximum()
    {
        var solution = new ArraySolution();
        var input = new int[] { 1, 5, 3 };
        var result = solution.FindMax(input);
        Assert.Equal(5, result);
    }

    [Fact]
    public void SumArray_ShouldCalculateSum()
